CREATE TABLE `exam`.`users` ( `user_id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT , `fullname` VARCHAR(100) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `email` VARCHAR(80) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `pass` VARCHAR(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL , `user_level` TINYINT(1) UNSIGNED NOT NULL , `active` VARCHAR(100) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL , `registration_date` DATETIME NOT NULL , `phone` VARCHAR(30) NULL DEFAULT NULL , `student_number` VARCHAR(30) NULL DEFAULT NULL , `picture_url` VARCHAR(255) NULL DEFAULT NULL , `language` VARCHAR(50) NULL DEFAULT NULL , `last_login` DATETIME NULL DEFAULT NULL , PRIMARY KEY (`user_id`)) ENGINE = InnoDB CHARACTER SET utf8 COLLATE utf8_general_ci;
